module appex.runtime {

    export class Runtime {
    
        public domains:appex.runtime.Domain[];

        constructor() {
            
            this.domains = [];

        }

        public add(domain:appex.runtime.Domain) : void {
        
            this.domains.push(domain);

        }
    }
}