module appex.timers {

    /** a stop watch used to time things */
    export class StopWatch {
        
        private starttime:Date;

        constructor() {
        
            this.start();
        }
        /** starts / resets the stopwatch. */
        public start() : void  {

            this.starttime = new Date();
        }
        /** stops the stop watch. 
        *
        *       returns - the time delta in milliseconds.
        */
        public stop () : number  {

            var stoptime = new Date();
            
            var offset = stoptime.getMilliseconds() - this.starttime.getMilliseconds();

            if(offset < 0) offset = -offset; // how does this happen?
                
            return offset;
        }
    }
}