/// <reference path="../references.ts" />

module appex {
    
    export class Route {
        
        public url     : string;   // the path to this route.

        public async    : boolean;  // the calling convention.

        public instance : any;      // the instance this route refers.

        public method   : string;   // the method in which to call.
        
        public input    : typescript.api.Type; // the input type.

        public output   : typescript.api.Type; // the output type.

        public call(input:any, callback:(output:any) => void) : any
        {
            return this.instance[this.method](input, callback);
        }
    }

}