/// <reference path="../references.ts" />

module appex.web {

    export interface IServerOptions {
        
        program         : string;

        devmode ?       : boolean;

        logging ?       : boolean; 

        context ?       : any;

        stdout  ?       : WritableStream;

        stderr  ?       : WritableStream;
    }

    export function NormalizeServerOptions(options:appex.web.IServerOptions) : appex.web.IServerOptions {

        if(options.program == null) {
        
             throw new Error('appex.web.IServerOptions: sourcefile is required.');   
        }

        if(options.devmode == null) {
        
            options.devmode = false;
        }

        if(options.logging == null) {
            
            options.logging = false;
        }

        if(options.context == null) {
        
            options.context = {};
        }

        if(options.stdout == null) {

            options.stdout =  process.stdout;

        }

        if(options.stderr == null) {
        
            options.stderr =  process.stderr;  
        }

        return options;
    }
}
