/// <reference path="../references.ts" />
/// <reference path="../timers/StopWatch.ts" />

module appex.web {

    /** a http request waiter. appex servers queue these when waiting on background processing to complete. */
    export class Waiter {
        
        /** a stop watch for timing. */
        public stopwatch : appex.timers.StopWatch;

        /** arguments:
        *       request  - the nodejs http request.
        *       response - the nodejs http response.
        *       next     - (optional) next function for express / connect.
        */
        constructor(public request  : http.ServerRequest,

                    public response : http.ServerResponse,

                    public next?    : Function ) {

            this.stopwatch = new appex.timers.StopWatch();

            this.stopwatch.start();
        }
    }
}