/// <reference path="../../references.ts" />
/// <reference path="../../modules/IModule.ts" />
/// <reference path="../routing/IRouter.ts" />
/// <reference path="IApp.ts" />
/// <reference path="Request.ts" />
/// <reference path="Response.ts" />

module appex.web.app {

    export class App implements appex.web.app.IApp {
        
        public server    : http.Server;

        public request   : appex.web.app.IRequest;

        public response  : appex.web.app.IResponse;

        public next      : Function;

        public module    : appex.modules.IModule;

        public router    : appex.web.routing.IRouter;

        public attribute : any;

        public mime      : appex.web.media.Mime; // should not be here.
        
        constructor(server          : http.Server,
             
                    serverRequest   : http.ServerRequest, 

                    serverResponse  : http.ServerResponse, 

                    next            : Function,

                    module          : appex.modules.IModule,

                    router          : appex.web.routing.IRouter) {
            
            this.server   = server;

            this.request  = new appex.web.app.Request(serverRequest);

            this.response = new appex.web.app.Response(serverResponse);

            this.next     = next;

            this.module   = module;

            this.router   = router;
        }
    }
}