/// <reference path="../../references.ts" />
/// <reference path="../../interfaces.ts" />

module appex.web.app {

    /** A appex http request */
    export interface IRequest {

        /** the nodejs server request */
        request     : http.ServerRequest;

        /** the http verb for this request. 
        *
        *       returns - the http verb for this request
        */
        method ()   : string;
        
        /** the http headers for this request.
        *
        *       returns - the http headers.
        */
        headers()   : appex.IDictionary<string, string>;

    }
}