/// <reference path="../../references.ts" />
/// <reference path="IRoute.ts" />

module appex.web.routing {

    /** appex router interface */
    export interface IRouter {
        
        /** the routes */
        routes: appex.web.routing.IRoute[];

        /** router http handler 
        *
        *   arguments:
        *       app      - the app context
        *       request  - the nodejs http request.
        *       response - the nodejs http response.
        */
        handler( app : any, request:http.ServerRequest, response:http.ServerResponse ) : boolean;
    }

}