var vm       = require('vm');

var delegate = null;

// initialization of kernel.

function setup(message) {
    
    process.mainModule.filename = message.filename;
    
    var sandbox = {	

        __filename  : __filename,

        __dirname   : __dirname,	    

        global	    : global,

        process     : process,

        require     : require,	

        console     : console,

        exports     : null,   
    };
	
    var script = vm.createScript('exports = ' + message.body.code, 'worker.js' );

    script.runInNewContext ( sandbox );
	
    delegate = sandbox.exports;
}

// invocation of delgate.
function call(message) {
    
    delegate(message.body, function(response) {

        process.send({

            messageid : message.messageid,

            type      : 'call',

            body      : response
        });
    });
}

// event handling.
process.on('message', function(message) {

    switch(message.type) {

        case "setup":

            setup(message);

            break;

        case "call":

            call(message);

            break;
    }    
});
